import 'package:bloc/bloc.dart';import 'package:bookapp_flutter/domain/repository/impl/app_repository_impl.dart';import 'package:meta/meta.dart';import '../../../../data/param/book_data.dart';import '../../../../domain/repository/app_repository.dart';part 'by_category_event.dart';part 'by_category_state.dart';class ByCategoryBloc extends Bloc<ByCategoryEvent, ByCategoryState> {  final AppRepository _repository = AppRepositoryImpl();  ByCategoryBloc() : super(ByCategoryInitial()) {    on<GetBooksByCategoryEvent>((event, emit) async {      try {        List<BookData> books =            await _repository.getBooksByCategory(event.category);        emit(GetBooksByCategoryState(books));      } catch (e) {        print(e);      }    });    on<BookToPlayEvent>((event, emit) {      emit(BookToPlayState(event.id));    });  }}